static BOOL	g_Load = FALSE;		// 初期化を行ったかのフラグ
static BG	g_BG;
int mapToShow;


// 初期化処理
//=============================================================================
HRESULT InitBG(void)
{
	ID3D11Device *pDevice = GetDevice();

	//テクスチャ生成
	for (int i = 0; i < TEXTURE_MAX; i++)
	{
		g_Texture[i] = NULL;
		D3DX11CreateShaderResourceViewFromFile(GetDevice(),
			g_TexturName[i],
			NULL,
			NULL,
			&g_Texture[i],
			NULL);
	}

	// 頂点バッファ生成
	D3D11_BUFFER_DESC bd;
	ZeroMemory(&bd, sizeof(bd));
	bd.Usage = D3D11_USAGE_DYNAMIC;
	bd.ByteWidth = sizeof(VERTEX_3D) * 4;
	bd.BindFlags = D3D11_BIND_VERTEX_BUFFER;
	bd.CPUAccessFlags = D3D11_CPU_ACCESS_WRITE;
	GetDevice()->CreateBuffer(&bd, NULL, &g_VertexBuffer);

	mapToShow = checkCurrentMap();

	g_BG.w = MAP_WIDTH;
	g_BG.h = MAP_HEIGHT;

	g_BG.texNo = mapToShow;
	g_BG.pos = D3DXVECTOR3(0.0f, 0.0f, 0.0f);
	g_BG.r = g_BG.g = g_BG.b = 1.0f;

	if (g_BG.texNo == FOREST)
	{
		PlaySound(SOUND_LABEL_BGM_forest);
	}
	else if (g_BG.texNo == DUNGEON)
	{
		PlaySound(SOUND_LABEL_BGM_dungeon);
	}

	g_Load = TRUE;	// データの初期化を行った
	return S_OK;
}

//=============================================================================
// 終了処理
//=============================================================================
void UninitBG(void)
{
	if (g_Load == FALSE) return;

	if (g_VertexBuffer)
	{
		g_VertexBuffer->Release();
		g_VertexBuffer = NULL;
	}

	for (int i = 0; i < TEXTURE_MAX; i++)
	{
		if (g_Texture[i])
		{
			g_Texture[i]->Release();
			g_Texture[i] = NULL;
		}
	}

	g_Load = FALSE;
}

//=============================================================================
// 更新処理
//=============================================================================
void UpdateBG(void)
{

}

//=============================================================================
// 描画処理
//=============================================================================
void DrawBG(void)
{
	// 頂点バッファ設定
	UINT stride = sizeof(VERTEX_3D);
	UINT offset = 0;
	GetDeviceContext()->IASetVertexBuffers(0, 1, &g_VertexBuffer, &stride, &offset);

	// マトリクス設定
	SetWorldViewProjection2D();

	// プリミティブトポロジ設定
	GetDeviceContext()->IASetPrimitiveTopology(D3D11_PRIMITIVE_TOPOLOGY_TRIANGLESTRIP);

	// マテリアル設定
	MATERIAL material;
	ZeroMemory(&material, sizeof(material));
	material.Diffuse = D3DXCOLOR(1.0f, 1.0f, 1.0f, 1.0f);
	SetMaterial(material);

	// BGの背景を描画
	{
		// テクスチャ設定
		GetDeviceContext()->PSSetShaderResources(0, 1, &g_Texture[g_BG.texNo]);

		// １枚のポリゴンの頂点とテクスチャ座標を設定
		SetSpriteLTColor(g_VertexBuffer,
			-g_BG.pos.x, -g_BG.pos.y, g_BG.w, g_BG.h, 
			0.0f, 0.0f, 1.0f, 1.0f,
			D3DXCOLOR(g_BG.r, g_BG.g, g_BG.b, 1.0f));

		// ポリゴン描画
		GetDeviceContext()->Draw(4, 0);
	}

}

int CheckMap(float x, float y)
{
	int posY = (int)floor(y / MAPCHIP_HEIGHT);
	int posX = (int)floor(x / MAPCHIP_WIDTH);
	int index = 0;

	switch (mapToShow)
	{
	case 0:
		index = g_MapChipJungle[posY][posX];
		break;
	case 1:
		index = g_MapChipDungeon[posY][posX];
		break;
	default:
		break;
	}


	return index;
}

int GetMapIndex(int y, int x)
{
	switch (mapToShow)
	{
	case 0:
		return g_MapChipJungle[y][x];
		break;
	case 1:
		return g_MapChipDungeon[y][x];
		break;
	default:
		break;
	}

	return -1;
}

